%% Análise do método probabilístico para epidemiaclear all;close all;clc;function [P] = Binomial(N,k,p)% aux = factorial(N)./(factorial(N-k)*factorial(k));aux = nchoosek (N, k);P = aux*p^k*(1-p)^(N-k)   end%% Características da populaçãoN = 10;p = 0.3;P = 0.7;%%%%% Modelo RF %%%%%TR = 10;T  = 5;for tr = 1 : TR  tr  t = 1;  I(t) = 1;  R(t) = 0;  S(t) = N - I(t);    while (S>1)&&(I>0)      for k = 0 : S(t)  Pk(k+1) = Binomial(S(t),k,p*P)  end    t = t + 1;  tsh = rand;    clc  aux2 = cumsum(Pk);   aux = aux2<tsh;   if sum(aux)==0   I(t) = 0;   else   I(t) = max(find(aux==1))   end      S(t) = S(t-1) - I (t);   R(t) = R(t-1) + I(t-1);  endwhile    Itr(tr,:) = I;    end                           