%Modelo Reed-Frost ngen=20;      % número de geraçõesSinit=2000;      % população suscetívelIinit=2;      % infectadosRinit=0;      % removidos / recuperadosq=0.999;nsims=10;     % número de simulaçõespkg load statisticsfor i=1:nsims      S=zeros(ngen,1);    I=zeros(ngen,1);    R=zeros(ngen,1);    S(1)=Sinit;    I(1)=Iinit;    R(1)=Rinit;    for j=1:ngen        I(j+1)=binornd(S(j),1-q^I(j));        S(j+1)=S(j)-I(j+1);        R(j+1)=R(j)+I(j);    end;    plot(0:ngen,I,'*-')    ylabel('Infectados');    xlabel('Gerações');    hold onend;          % final da simulação